import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'
import styled from 'styled-components'
import '../style/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class TeamsList extends Component {
    constructor(props) {
        super(props);
        this.state = {
          teams: []
        }
      }

      componentDidMount() {
        fetch('http://localhost:8080/teams')
        .then(res => res.json())
        .then(json => json.teams)
        .then(teams => this.setState({ 'teams': teams }))
      }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.get('/teams', { responseType: 'json' }).then(teams => {
            this.setState({
                tableData: teams.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { teams } = this.state
        console.log('TCL: TeamsList -> render -> teams', teams)
        const Table = ({ teams }) => {
            return (
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Team Members</th>
                    <th>Score</th>
                  </tr>
                </thead>
                <tbody>
                  { (teams.length > 0) ? teams.map( (teams, index) => {
                     return (
                      <tr key={ index }>
                        <td>{ teams.name }</td>
                        <td>{ teams.teamMembers }</td>
                        <td>{ teams.score}</td>
                      </tr>
                    )
                   }) : <tr><td colSpan="5">Loading...</td></tr> }
                </tbody>
              </table>
            );
          }

        return (
            <Wrapper>
                    <Table teams={ this.state.teams } />
            </Wrapper>
        )
    }
}

export default TeamsList
