import React, { Component } from 'react'
import api from '../api'
import styled from 'styled-components'

const Container = styled.div.attrs({
    className: 'container',
})`
    background-color: transparent;
    padding: 2rem;
`
const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

/**
 * Login component
 */
class LogIn extends Component {
    constructor() {
        super();
        this.state = {
            teams: [],
            name: '',
            isLogIn: false,
            validationError: '',
            showButton: false
        };
    }


    //Fetching list of teams
    componentDidMount() {
        fetch("http://localhost:8080/teams")
        .then((response) => {
            return response.json();
        })
        .then(data => {
            let teamsFromApi = data.map(team => {
                return {value: team.name, display: team.name}
            });
            this.setState({
                teams: teamsFromApi
            });
        }).catch(error => {
            console.log(error);
        });
    }

    //to update the team as login
    handleDropdownChange(e) {

        const isLogIn = true;
        const payload = {name, isLogIn}

        //if User does not select a team
        if (e.target.value === "") {
            validationError = "You must select your favourite team"
        }

        this.setState({ 
            name: e.target.value,

        });

    }

    handleLogInTeam = async () => {
        const {name} = this.state;
        const isLogIn = true;
        const payload = {isLogIn}


        await api.updateTeamByName(name, payload).then(res => {
            //window.alert(`Team updated successfully ` + payload.name)
            this.setState({
                name: '',
                isLogIn: '',
            })
        })
    }

    render() {
        return (
        <Container class="container">
          <div>
                <DropDownList
                    data={this.teams}
                    textField="text"
                    dataItemKey="id"
                    value={this.state.value}
                    onChange={this.handleChange}
                />
              <Button onClick={this.handleLogInTeam}>Sign in</Button>
          </div>
          <div style={{color: 'red', marginTop: '5px'}}> 
                {this.state.validationError} 
          </div>
        </Container>    
        )
      }


}

export default LogIn
