        return (
            <Wrapper>
                <ul>
                    {
                         this.state.te.map((eachCourse) => {
                            return <li key={eachCourse.id}>{eachCourse.title}</li>
                        })
                    }
                </ul>
            </Wrapper>
        )
    }
}

export default TeamsList
