import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'
import styled from 'styled-components'
import '../style/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class TeamsList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            tableData: [{
                name: '',
                teamMembers: '',
                score: '',
            }],
            isLoading: false,
        };

        //this.state = {
        //    teams: [],
        //    columns: [],
        //    isLoading: false,
        //}
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllTeams().then(teams => {
            this.setState({
                tableData: teams.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { teams, isLoading } = this.state
        console.log('TCL: TeamsList -> render -> teams', teams)

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={teams}
                        columns={[
                            {
                                Header: 'Team Name',
                                accessor: 'name',
                                //filterable: true,
                            },
                            {
                                Header: 'Team Members',
                                accessor: 'teamMembers',
                                //filterable: true,
                            },
                            {
                                Header: 'Score',
                                accessor: 'score',
                                //filterable: true,
                            },
                        ]}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )}
            </Wrapper>
        )
    }
}

export default TeamsList
