import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'
import styled from 'styled-components'
import '../style/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class TeamsList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            teams: []
          };
          this.header = [
            { title: "Name", prop: "name", filterable: true },
            {
              title: "Team Names",
              prop: "teamMembers",
              filterable: true
            },
            { title: "Score", prop: "score", sortable: true, filterable: true }
          ];
      }

      componentDidMount() {
        fetch("http://localhost:8080/teams")
        .then(res => res.json())
        .then(
        result => {
          this.setState({
            isLoaded: true,
            items: result
          });
        },
        error => {
          this.setState({
            isLoaded: true,
            error: error
          });
        }
      );
      }



    render() {
        const { teams } = this.state
        console.log('TCL: TeamsList -> render -> teams', teams)
        const Table = ({ teams }) => {
            return (
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Team Members</th>
                    <th>Score</th>
                  </tr>
                </thead>
                <tbody>
                  { (teams.length > 0) ? teams.map( (teams, index) => {
                     return (
                      <tr key={ index }>
                        <td>{ teams.name }</td>
                        <td>{ teams.teamMembers }</td>
                        <td>{ teams.score}</td>
                      </tr>
                    )
                   }) : <tr><td colSpan="5">Loading...</td></tr> }
                </tbody>
              </table>
            );
          }

        return (
            <Wrapper>
                    <Table teams={ this.state.teams } />
            </Wrapper>
        )
    }
}

export default TeamsList
