import React, { Component } from 'react'
import api from '../api'

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class TeamsUpdate extends Component {
    constructor(props) {
        super(props)

        this.state = {
            _id: '',
            name: this.props.match.params.name,
            teamMembers: '',
            score: '',
        }
    }

    handleChangeInputName = async event => {
        const name = event.target.value
        this.setState({ name })
    }

    handleChangeInputTeamMembers = async event => {
        const teamMembers = event.target.value
        this.setState({ teamMembers })
    }

    handleUpdateTeam = async () => {
        const { _id, name, teamMembers } = this.state
        const payload = { _id, name, teamMembers}

        await api.updateTeamById(payload).then(res => {
            window.alert(`Team updated successfully`)
            this.setState({
                name: '',
                teamMembers: '',
            })
        })
    }

    componentDidMount = async () => {
        const { name } = this.state
        const team = await api.getTeamByName(name)

        this.setState({
            name: team.data.data.name,
            teamMembers: team.data.data.teamMembers
        })
    }

    render() {
        const { name, teamMembers } = this.state
        return (
            <Wrapper>
                <Title>Update Movie</Title>

                <Label>Team Name: </Label>
                <InputText
                    type="text"
                    value={name}
                    onChange={this.handleChangeInputName}
                />

                <Label>Team Members: </Label>
                <InputText
                    type="text"
                    value={teamMembers}
                    onChange={this.handleChangeInputTeamMembers}
                />

                <Button onClick={this.handleUpdateTeam}>Update Team</Button>
                <CancelButton href={'/movies/list'}>Cancel</CancelButton>
            </Wrapper>
        )
    }
}

export default TeamsUpdate
