import React, { Component } from 'react'
import api from '../api'
import styled from 'styled-components'

const Container = styled.div.attrs({
    className: 'container',
})`
    background-color: transparent;
    padding: 2rem;
`
const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

/**
 * Login component
 */
class LogIn extends Component {
    constructor() {
        super();
        this.state = {
            teams: [],
            name: '',
            isLogIn: false,
            validationError: '',
            showButton: false
        };
    }


    //Fetching list of teams
    componentDidMount() {
        fetch("http://localhost:8080/teams")
        .then((response) => {
            return response.json();
        })
        .then(data => {
            let teamsFromApi = data.map(team => {
                return {value: team.name, display: team.name}
            });
            this.setState({
                teams: teamsFromApi[0].name
            });
        }).catch(error => {
            console.log(error);
        });
    }

    //to update the team as login
    async handleDropdownChange(e) {

        //get the team selected
        const name = e.target.value
        //set it as login
        const isLogIn = true

        this.setState({ 
            name,
            isLogIn 
        }); 

        const payload = {name, isLogIn}

        api.updateTeamByName(name, payload).then(res => {
            //window.alert(`Team updated successfully ` + payload.name)
            this.setState({
                name: '',
                isLogIn: '',
            })
        })


    }



    render() {
        const validationError = "You must select your favorite team"
        return (

        <Container class="container">
          <div>
              <select 
                value={this.state.selectedTeam}
                onChange={this.handleDropdownChange}>
                {this.state.teams.map((team) => <option key={team.value} value={team.value}>{team.display}</option>)}
              </select>
          </div>

          <div style={{color: 'red', marginTop: '5px'}}> 
                {this.state.validationError} 
          </div>
        </Container>    
        )
      }


}

export default LogIn
