import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Route, withRouter} from 'react-router-dom';


const Container = styled.div.attrs({
    className: 'container',
})`
    background-color: transparent;
    padding: 2rem;
`


/**
 * Login component
 */
class LogIn extends Component {
    constructor() {
        super();
        this.state = {
            teams: [],
        };
    }


    //Fetching list of teams
    componentDidMount() {
        fetch("http://localhost:8080/teams")
        .then((response) => {
            return response.json();
        })
        .then(data => {
            let teamsFromApi = data.map(team => {
                return {value: team.name, display: team.name}
            });
            this.setState({
                teams: [{value: '', display: '(Select your favourite team)'}].concat(teamsFromApi)
            });
        }).catch(error => {
            console.log(error);
        });
    }


    render() {
        return (
          <div>
            <select>
              {this.state.teams.map((team) => <option key={team.value} value={team.value}>{team.display}</option>)}
            </select>
          </div>
        )
      }


}

export default LogIn
