import axios from 'axios'


const api = axios.create({
    baseURL: 'http://localhost:8080',
    withCredentials: true,
})

axios.defaults.headers.post['Content-Type'] = 'application/x-www-form-urlencoded';
axios.defaults.withCredentials = true;
axios.defaults.crossDomain = true;
//axios.


//app.use(cors())

export const insertTeam = payload => api.post(`/teams`, payload)
export const getAllTeams = () => api.get(`/teams`, {
    headers: {
        'Access-Control-Allow-Origin' : '*',
        'Access-Control-Allow-Credentials': true
      },
      proxy: {
        host: 'localhost',
        port: 3000
      }
})
export const getTeamById = id => api.get(`/teams/${id}`)
export const getTeamByName = name => api.get(`/teams/${name}`)
export const updateTeamById = (id, payload) => api.put(`/teams/${id}`, payload)
export const deleteTeamById = id => api.delete(`/teams/${id}`)
export const deleteAllTeams = id => api.delete(`/teams`)

const apis = {
    insertTeam,
    getAllTeams,
    getTeamById,
    getTeamByName,
    updateTeamById,
    deleteTeamById,
    deleteAllTeams
}

export default apis
