import React, { Component } from 'react'
import Datatable from "react-bs-datatable";
import api from '../api'
import styled from 'styled-components'
import '../style/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class TeamsList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            teams: []
          };
          this.header = [
            { title: "Name", prop: "name", filterable: true },
            {
              title: "Team Names",
              prop: "teamMembers",
              filterable: true
            },
            { title: "Score", prop: "score", sortable: true, filterable: true }
          ];
      }

      componentDidMount = async () => {
        fetch("localhost:8080/teams")
        .then(res => res.json())
        .then(
        result => {
          this.setState({
            isLoaded: true,
            teams: result
          });
        },
        error => {
          this.setState({
            isLoaded: true,
            error: error
          });
        }
      );
      }



      render() {
        const { error, isLoaded, teams } = this.state;
        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          console.log(this.state.teams);
          return (
            <Wrapper>
              <Datatable
                tableHeader={this.header}
                tableBody={this.state.teams}
                keyName="userTable"
                tableClass="striped hover responsive"
                rowsPerPage={3}
                rowsPerPageOption={[3, 5, 8, 10]}
                initialSort={{ prop: "names", isAscending: true }}
              />
            </Wrapper>
          );
        }
      }
    }

export default TeamsList
