import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'

import styled from 'styled-components'

//import 'react-table/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`


class TeamsList extends Component {

    constructor(props) {
        super(props)
        this.state = {
            teams: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        api.getAllTeams().then( (response) => {
            const data = response.data;
            this.setState({
                teams: data,
                isLoading: false,
            })
            window.alert(`Teams have been received`)
        })
        .catch( () => {
            alert('Error while retrieving the teams');
        });
    }


    render() {
        const { teams , isLoading } = this.state 
        window.alert(`Teams page have been loaded`)
        return (
            <Wrapper>
                <ul>
                    {
                         this.state.teams.map((eachTeam) => {
                            return <li key={eachTeam.name}>{eachTeam.teamMembers}</li>
                        })
                    }
                </ul>
            </Wrapper>
        )
    }
}

export default TeamsList
