                    <select
                value={this.state.name}
                onChange={e =>
                        this.setState({
                                name: e.target.value,
                                isLogIn: 
                                        e.target.value === ""
                                        ? false
                                        : true,
                                validationError:
                                        e.target.value === ""
                                        ? "You must select your favourite team"
                                        : ""
                        })
                    }
            >
                {this.state.teams.map(team => (
                    <option
                        key={team.value}
                        value={team.value}
                    >
                    {team.display}
                    </option>
                ))}
            </select>
          </div>

          <div style={{color: 'red', marginTop: '5px'}}> 
                {this.state.validationError} 
          </div>

        <Button type="submit" className="btn btn-primary" onClick={this.handleOnSubmit}>Sign In</Button> }}*/


