import React, { Component } from 'react'
import { useTable } from "react-table";
import api from '../api'
import styled from 'styled-components'
import '../style/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class TeamsList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            teams: []
          };
          this.header = [
            { title: "Name", prop: "name", filterable: true },
            {
              title: "Team Names",
              prop: "teamMembers",
              filterable: true
            },
            { title: "Score", prop: "score", sortable: true, filterable: true }
          ];
      }

      componentDidMount() {
          fetch("http://localhost:8080/teams")
            .then(res => res.json())
            .then(
            result => {
                this.setState({
                isLoaded: true,
                teams: result
                });
            },
            error => {
                this.setState({
                isLoaded: true,
                error: error
            });
            }
        );
      }



      render() {
        const { error, isLoaded, teams } = this.state;
        const teamsData = this.state.teams;

        const columns = [
            {
                Header: 'Name',
                accessor: 'name',
                filterable: true,
            },
            {
                Header: 'Team Members',
                accessor: 'teamMembers',
                filterable: true,
            },
            {
                Header: 'Score',
                accessor: 'score',
                filterable: true,
            },
        ]

        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          console.log(teamsData);

          return (
            <Wrapper>
              <div>
              {
					teamsData.map((team, i) => {
						return (
							<div key={i}>
								<div>
									<div>
										<div>
											<a href={experience.url}>{experience.companyName}</a>
										</div>
											{experience.roles.map(function (role, i) { 
												return <div key={i}>
													<h5>{role.title}</h5>
													<span>{role.startDate}</span>
													<span>{role.location}</span>
													<p>{role.description}</p>
												</div>
											})}
									</div>
								</div>
							</div>
						);
					})
				}
                </div>
            </Wrapper>
          );
        }
      }
    }

export default TeamsList
