import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'
import styled from 'styled-components'
import '../style/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class TeamsList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            teams: []
          };
          this.header = [
            { title: "Name", prop: "name", filterable: true },
            {
              title: "Team Names",
              prop: "teamMembers",
              filterable: true
            },
            { title: "Score", prop: "score", sortable: true, filterable: true }
          ];
      }

      async componentDidMount() {
        const res = await  fetch(api.getAllTeams)
        const teamsData = await res.json()
        teamsData => {
          this.setState({
            isLoaded: true,
            teams: teamsData
          });
        },
        error => {
          this.setState({
            isLoaded: true,
            error: error
          });
        }

      }



      render() {
        const { error, isLoaded, teams } = this.state;
        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          console.log(this.state.teams);
          return (
            <Wrapper>
              <h1> Hello </h1>
              <p>Team name + {this.state.teams.name}</p>
            </Wrapper>
          );
        }
      }
    }

export default TeamsList
