import React, { Component } from 'react'
import api from '../api'
import styled from 'styled-components'
import { LayoutComponent } from '../components'

const Container = styled.div.attrs({
    className: 'container',
})`
    background-color: transparent;
    padding: 2rem;
`
const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

/**
 * Login component
 */
class LogIn extends Component {
    constructor() {
        super();
        this.state = {
            teams: [],
            name: '',
            isLogIn: '',
            validationError: '',
            displayQuiz: ''
        };
    }


    //Fetching list of teams
    componentDidMount() {
        fetch("http://localhost:8080/teams")
        .then((response) => {
            return response.json();
        })
        .then(data => {
            let teamsFromApi = data.map(team => {
                return {value: team.name, display: team.name}
            });
            this.setState({
                teams: [{value: '', display: '(Select your Team)'}].concat(teamsFromApi)
            });
        }).catch(error => {
            console.log(error);
        });
    }


    //to update the team as login
    handleDropdownChange = async () => {
        if(this.state.isLogIn === false){
            this.setState({isLogIn: true})
            console.log(this.state.name + " is logged in? " + this.state.isLogIn ) 

            const {name, isLogIn } = this.state
            const payload = {isLogIn}

             await api.logInTeam(name, payload).then(res => {
                window.alert(`Team logged in successfully ` + payload.isLogIn)
                this.setState({
                    isLogIn: '',
                    name: '',
                    score: '',
                    teamMembers: '',
                    displayQuiz: true
                })
             })
        }
        else{
            this.setState({
                validationError: "The Team members cannot logged in as it is already logged in.",
                displayQuiz: false
            })
        }



    }

    handleOnSubmit = async () => {

    }



    render() {

        return (
            <LayoutComponent>
                <Container class="container">
                    <div>
                    <select
                value={this.state.name}
                onChange={e =>
                        this.setState({
                                name: e.target.value,
                                isLogIn: 
                                        e.target.value === ""
                                        ? false
                                        : true,
                                validationError:
                                        e.target.value === ""
                                        ? "You must select your favourite team"
                                        : ""
                        })
                    }
            >
                {this.state.teams.map(team => (
                    <option
                        key={team.value}
                        value={team.value}
                    >
                    {team.display}
                    </option>
                ))}
            </select>
          </div>

          <div style={{color: 'red', marginTop: '5px'}}> 
                {this.state.validationError} 
          </div>

        <Button onClick={this.handleDropdownChange}>Sign In</Button>
        </Container> 

            </LayoutComponent>

        )
      }


}

export default LogIn
