import React, { Component } from 'react'
import ReactTable from "../components/ReactTable";  
import styled from 'styled-components'
import '../style/teamslist-page.css';
import * as constants from "../app/constantFile"
import LogIn from "./LogIn"

const Container = styled.div.attrs({
    className: 'container',
})`
    background-color: transparent;
    padding: 2rem;
`

class TeamsList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            teams: []
        };
      }

      componentDidMount() {
          fetch("http://localhost:8080/teams")
            .then(res => res.json())
            .then(
            result => {
                this.setState({
                isLoaded: true,
                teams: result
                });
            },
            error => {
                this.setState({
                isLoaded: true,
                error: error
            });
            }
        );
      }



      render() {
        const { error, isLoaded, teams } = this.state;

        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {           

            //Declaring columns
            const columns = [{  
                Header: 'Name',  
                accessor: 'name'  
               },{  
                Header: 'Team Members',  
                accessor: 'teamMembers'  
               },{  
                Header: 'Score',  
                accessor: 'score'  
            }]

            return (
              <div>
              <Container class="container">
                    <span></span>
                    <h1 id="title"> Teams for <span id="pq-title">{constants.pubquiz_Title}</span> co-hosted with <span id="pq-cohost"> {constants.pubquiz_Cohost}  </span></h1>
                    <span></span>
                    <div>  
                     <ReactTable  
                       data={teams}  
                       columns={columns} 
                        />  
                    </div>   
                </Container>    
                <Input type="select" label="Multiple Select" multiple>
                  <option value="select">select (multiple)</option>
                  <option value="other">...</option>
                  </Input>
                </div>
           );      



        }
      }
    }

export default TeamsList
