import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'
import styled from 'styled-components'
import '../style/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class TeamsList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            teams: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllTeams().then(teams => {
            teams.setHeader("Access-Control-Allow-Origin", "*");
            teams.setHeader("Access-Control-Allow-Credentials", "true");
            teams.setHeader("Access-Control-Max-Age", "1800");
            teams.setHeader("Access-Control-Allow-Headers", "content-type");
            teams.setHeader("Access-Control-Allow-Methods","PUT, POST, GET, DELETE, PATCH, OPTIONS");

            this.setState({
                teams: teams.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { teams, isLoading } = this.state
        console.log('TCL: TeamsList -> render -> teams', teams)

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Team Name',
                accessor: 'name',
                filterable: true,
            },
            {
                Header: 'Team members',
                accessor: 'teamMembers',
                filterable: true,
            },
            {
                Header: 'Score',
                accessor: 'score',
                Cell: props => <span>{props.value.join(' / ')}</span>,
            },
        ]

        let showTable = true
        if (!teams.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={teams}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )}
            </Wrapper>
        )
    }
}

export default TeamsList
