import React, { Component } from 'react'
import { useTable } from "react-table";
import api from '../api'
import styled from 'styled-components'
import '../style/react-table.css'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

class TeamsList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            teams: []
          };
      }

      componentDidMount() {
          fetch("http://localhost:8080/teams")
            .then(res => res.json())
            .then(
            result => {
                this.setState({
                isLoaded: true,
                teams: result
                });
            },
            error => {
                this.setState({
                isLoaded: true,
                error: error
            });
            }
        );
      }



      render() {
        const { error, isLoaded, teams } = this.state;
        const teamsData = this.state.teams;

        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          console.log(teams);

          return (
            <Wrapper>
              <div>
              {
					teams.map((team, i) => {
						return (
							<div key={i}>
								<div>
                                    <h1>{team.name}</h1>
									<div>
										<div>
                                            <h5>{team.teamMembers}</h5>
                                            <h5>{team.score}</h5>
										</div>
									</div>
								</div>
                                <span></span>
							</div>
						);
					})
				}
                </div>
            </Wrapper>
          );
        }
      }
    }

export default TeamsList
