import React, { Component } from 'react'
import ReactTable from "../components/ReactTable";  
import styled from 'styled-components'
import '../style/teamslist-page.css';
import * as constants from "../app/constantFile"

const Container = styled.div.attrs({
    className: 'container',
})`
    background-color: transparent;
    padding: 2rem;
`

class TeamsList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            teams: []
        };
      }

      componentDidMount() {
          fetch("http://localhost:8080/teams")
            .then(res => res.json())
            .then(
            result => {
                this.setState({
                isLoaded: true,
                teams: result
                });
            },
            error => {
                this.setState({
                isLoaded: true,
                error: error
            });
            }
        );
      }



      render() {
        const { error, isLoaded, teams } = this.state;
        //const teamsData = this.state.teams;

        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
            console.log(teams);

            //Declaring columns
            const columns = [{  
                Header: 'Name',  
                accessor: 'name'  
               },{  
                Header: 'Team Members',  
                accessor: 'teamMembers'  
               },{  
                Header: 'Score',  
                accessor: 'score'  
            }]

            return (
              <Container class="container">
                    <span></span>
                    <h1 id="title"> Teams for <span id="pq-title">{constants.pubquiz_Title}</span> co-hosted with <span id="pq-cohost"> {constants.pubquiz_Cohost}  </span></h1>
                    <span></span>
                    <div>  
                     <ReactTable  
                       data={teams}  
                       columns={columns} 
                        />  
                    </div>   
                    </Container>     
           );      

          /* return (
            <Wrapper>
              <div>
              {
					teams.map((team, i) => {
						return (
							<div key={i}>
								<div>
                                    <h1>{team.name}</h1>
									<div>
										<div>
                                            <h5>{team.teamMembers}</h5>
                                            <h5>{team.score}</h5>
										</div>
									</div>
								</div>
                                <span></span>
							</div>
						);
					})
				}
                </div>
            </Wrapper>
          ); */

        }
      }
    }

export default TeamsList
